import {useState, useEffect, useContext} from 'react'
import {Link, useParams} from 'react-router-dom'
import Cookies from 'js-cookie'
import Loader from 'react-loader-spinner'
import {BsPlusSquare, BsDashSquare} from 'react-icons/bs'
import Context from '../Context'
import Header from '../Header'
import SimilarProductItem from '../SimilarProductItem'

import './index.css'

const apiStatusConstants = {
  initial: 'INITIAL',
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

const ProductItemDetails = () => {
  const [productData, setProductData] = useState({})
  const [similarProductsData, setSimilarProductsData] = useState([])
  const [apiStatus, setApiStatus] = useState(apiStatusConstants.initial)
  const [quantity, setQuantity] = useState(1)
  const {addItem} = useContext(Context)
  const [buttonName, setButtonName] = useState('Add TO CART')

  const {id} = useParams()

  const getFormattedData = data => ({
    availability: data.availability,
    brand: data.brand,
    description: data.description,
    id: data.id,
    imageUrl: data.image_url,
    price: data.price,
    rating: data.rating,
    title: data.title,
    totalReviews: data.total_reviews,
  })

  useEffect(() => {
    const getProductData = async () => {
      setApiStatus(apiStatusConstants.inProgress)
      const jwtToken = Cookies.get('jwt_token')
      const apiUrl = `https://apis.ccbp.in/products/${id}`
      const options = {
        headers: {
          Authorization: `Bearer ${jwtToken}`,
        },
        method: 'GET',
      }

      const response = await fetch(apiUrl, options)
      if (response.ok) {
        const fetchedData = await response.json()
        const updatedData = getFormattedData(fetchedData)
        const updatedSimilarProductsData = fetchedData.similar_products.map(
          eachSimilarProduct => getFormattedData(eachSimilarProduct),
        )
        setProductData(updatedData)
        setSimilarProductsData(updatedSimilarProductsData)
        setApiStatus(apiStatusConstants.success)
      } else {
        setApiStatus(apiStatusConstants.failure)
      }
    }
    getProductData()
  }, [id]) // Dependency array to ensure useEffect runs when id changes

  const onDecrementQuantity = () => {
    setQuantity(prevQuantity =>
      prevQuantity > 1 ? prevQuantity - 1 : prevQuantity,
    )
  }

  const onIncrementQuantity = () => {
    setQuantity(prevQuantity => prevQuantity + 1)
  }

  const renderLoadingView = () => (
    <div className='products-details-loader-container' data-testid='loader'>
      <Loader type='ThreeDots' color='#0b69ff' height='50' width='50' />
    </div>
  )

  const renderFailureView = () => (
    <div className='product-details-error-view-container'>
      <img
        alt='error view'
        src='https://assets.ccbp.in/frontend/react-js/nxt-trendz-error-view-img.png'
        className='error-view-image'
      />
      <h1 className='product-not-found-heading'>Product Not Found</h1>
      <Link to='/products'>
        <button type='button' className='button'>
          Continue Shopping
        </button>
      </Link>
    </div>
  )

  const renderProductDetailsView = () => {
    const {
      availability,
      brand,
      description,
      imageUrl,
      price,
      rating,
      title,
      totalReviews,
    } = productData

    return (
      <div className='product-details-success-view'>
        <div className='product-details-container'>
          <img src={imageUrl} alt='product' className='product-image' />
          <div className='product'>
            <h1 className='product-name'>{title}</h1>
            <p className='price-details'>Rs {price}/-</p>
            <div className='rating-and-reviews-count'>
              <div className='rating-container'>
                <p className='rating'>{rating}</p>
                <img
                  src='https://assets.ccbp.in/frontend/react-js/star-img.png'
                  alt='star'
                  className='star'
                />
              </div>
              <p className='reviews-count'>{totalReviews} Reviews</p>
            </div>
            <p className='product-description'>{description}</p>
            <div className='label-value-container'>
              <p className='label'>Available:</p>
              <p className='value'>{availability}</p>
            </div>
            <div className='label-value-container'>
              <p className='label'>Brand:</p>
              <p className='value'>{brand}</p>
            </div>
            <hr className='horizontal-line' />
            <div className='quantity-container'>
              <button
                type='button'
                className='quantity-controller-button'
                onClick={onDecrementQuantity}
                data-testid='minus'
              >
                <BsDashSquare
                  className='quantity-controller-icon'
                  aria-label='minus'
                />
              </button>
              <p className='quantity'>{quantity}</p>
              <button
                type='button'
                className='quantity-controller-button'
                onClick={onIncrementQuantity}
                data-testid='plus'
              >
                <BsPlusSquare
                  className='quantity-controller-icon'
                  aria-label='plus'
                />
              </button>
            </div>
            <button
              type='button'
              onClick={add}
              className='button add-to-cart-btn'
            >
              {buttonName}
            </button>
          </div>
        </div>
        <h1 className='similar-products-heading'>Similar Products</h1>
        <ul className='similar-products-list'>
          {similarProductsData.map(eachSimilarProduct => (
            <SimilarProductItem
              productDetails={eachSimilarProduct}
              key={eachSimilarProduct.id}
            />
          ))}
        </ul>
      </div>
    )
  }
  const add = () => {
    const h = {
      title: productData.title,
      brand: productData.brand,
      id: productData.id,
      imgUrl: productData.imageUrl,
      price: productData.price,
      qu: quantity,
    }
    addItem(h)
    setButtonName('ADDED TO CART')
  }
  const renderProductDetails = () => {
    switch (apiStatus) {
      case apiStatusConstants.success:
        return renderProductDetailsView()
      case apiStatusConstants.failure:
        return renderFailureView()
      case apiStatusConstants.inProgress:
        return renderLoadingView()
      default:
        return null
    }
  }

  return (
    <>
      <Header />
      <div className='product-item-details-container'>
        {renderProductDetails()}
      </div>
    </>
  )
}

export default ProductItemDetails
